import { useEffect, useState } from 'react'

import classes from './AvailableMeals.module.css'

import MealItem from './MealItem/MealItem'
import Card from '../UI/Card'

export default function AvailableMeals() {
  const [meals, setMeals] = useState([])

  // sending http request is asynchronous task so fetch returns a promise
  useEffect(() => {
    // the function you pass to useEffect should not return a promise, the solution is creating a new function
    const fetchMeals = async () => {
      const response = await fetch('https://react-http-6b4a6.firebaseio.com/meals.json')
      const responseData = await response.json()

      const loadedMeals = []

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price
        })
      }

      setMeals(loadedMeals)
    }

    fetchMeals()
  }, [])

  const mealsList = meals.map(meal => <MealItem key={meal.id} id={meal.id} name={meal.name} description={meal.description} price={meal.price} />)

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  )
}

